      // 真实dom创建后
      mounted() {
        console.log('mounted');
      },
      // 数据更新之前
      beforeUpdate() {
        console.log("beforeUpdate");
      },
      // 数据更新之后
      updated() {
        console.log("beforeUpdate");
      },
      // 马上销毁
      beforeDestroy() {
