template>

script>
import {nanoid} from 'nanoid'

export default {
  name: "MyHeader",
  data() {
      return {
          title: ''
      }
  },
  methods: {
    add(e) {
        if (!this.title.trim) return

        const todoObj = {id: nanoid(), name:this.title, done:false}

        this.addTodo(todoObj)

        this.title = ''
    }
  },
  props: ['addTodo']
};
script>

