script>
export default {
  name: "MyFooter",
  props: ['todos', 'checkedAllTodo', 'clearTodos'],
  computed: {
    isAll(){
      return this.doneTotals === this.total && this.total > 0
    },
    isAll2:{
      get(){
        return this.doneTotals === this.total && this.total > 0
      },
      set(value) {
        this.checkedAllTodo(value)
      }
    },
    total() {
      return this.todos.length
    },
    doneTotals(){
      return this.todos.reduce((pre, todo) => {return pre+=todo.done}, 0)
    }
  },
  methods: {
    checkedAll(e){
      this.checkedAllTodo(e.target.checked)
    },
    clearAllTodos() {
      this.clearTodos()
    }
  },
};
script>

