  script>
    Vue.config.productionTip = false

    new Vue({
      el: '#root',
      data: { 
        firstName: '张',
        lastName: '三'
      }, 
      computed:{
        fullName:{
          // get:当有人读取时，get就会被调用
          // 初次读取时被调用（有缓存），所依赖得值发生变化也会调用
          get(){
            return this.firstName + '-' + this.lastName
          },

          // set 当fullname被修改时被调用
          set(value){
            const arr = value.split('-')
            this.firstName = arr[0]
            this.lastName = arr[1]
          }
        }
      }
    })
