      el: '#root',
      data: {
        sortType: 0, // 0:原顺序 1：降序 2：升序
        keyWord: '',
        persons: [
          { id: '001', name: '张三', age: 21, sex: '男' },
          { id: '002', name: '李四', age: 31, sex: '男' },
