  div id="root">
    h2>当前的n值是:{{n}}h2>
    button @click="add">点我+1button>
    button @click="bye">点我销毁button>
  div>
  script>
    Vue.config.productionTip = false

    new Vue({
      el: '#root',
      data: {
        n: 0
      },
      methods: {
        add(){
          this.n++
        },
        bye(){
          console.log('bye');
          this.$destroy() // 清理它与其它实例的连接，解绑它的全部指令及自定义事件监听器， 原生的不会销毁
        }
      },
      // 无数据代理，无方法
      beforeCreate() {
        console.log('beforeCreate');
      },
      // 有数据代理和对应方法
      created() {
        console.log('created');
      },
      // 解析完模板，虚拟dom创建后
      beforeMount() {
        console.log('beforeMount');
      },
      // 真实dom创建后
      mounted() {
        console.log('mounted');
      },
      // 数据更新之前
      beforeUpdate() {
        console.log("beforeUpdate");
      },
      // 数据更新之后
      updated() {
        console.log("beforeUpdate");
      },
      // 马上销毁
      beforeDestroy() {
        // 
        console.log("beforeDestroy");
      },
      destroyed() {
        console.log("destroyed");
      }
    })


  script>
