body>
  div id="root">
    h2 :style="{opacity}">{{name}}h2>
    button @click="stop">点我停止变幻button>
  div>
  script>
    Vue.config.productionTip = false

    new Vue({
      el: '#root',
      data: {
        name: '南图小学',
        opacity: 0.6
      },
      methods: {
        stop() {
          this.$destroy()
        }
      },
      // DOM渲染之后
      mounted() {
        this.timer = setInterval(() => {
          this.opacity -= 0.01
          if (this.opacity 0) this.opacity = 1
        }, 16)
      },
      beforeDestroy() {
        clearInterval(this.timer)
      },
    })
  script>
