          schoolAddress: '金城'
        }
      },
      methods: {
        tipSchool() {
          alert(this.schoolName)
        }
      },
    })

    const student = Vue.extend({ 
      template: `
        div>
          h2>学生姓名:{{name}}h2>
          h2>学生年龄:{{age}}h2>  
        div>
      `,
      data() {
        return {
          name: '小张',
          age: 30
        }
      }
