    const hello = Vue.extend({
      name: 'helloworld', // 开发者工具显示自定义名称
      template: `
        div>
          h2>你好h2>
        div>
      `
    })

    //注册全局 所有vm都可以使用
    Vue.component('hello', hello)

    new Vue({
      el: '#root',
      data:{
        msg: '你好'
      },
      components:{
        school,
        student
      }
