    /*
    一个重要内置关系： VueComponent.prototype.__proto__ === Vue.prototype
        便于 vc 可以访问到 Vue 原型上的实例和方法
    */

    Vue.config.productionTip = false
    Vue.prototype.headname = '只能这么用'

    const school = Vue.extend({
      template:`

          学校名称:{{schoolName}}
          学校地址:{{schoolAddress}} 
          {{headname}} 

      `,
      data() {
