      console.log('有人发布了hello消息', msgName, data);
    })
  },
  beforeDestroy() {
    // this.$bus.$off("hello");

    // 两种方式均可取消订阅
    pubsub.unsubscribe(this.pubID)
    // pubsub.unsubscribe('hello')
  },
  methods: {
    death(){
      // this.$destroy()
      pubsub.unsubscribe(this.pubID)

      // pubsub.unsubscribe('hello')
      console.log('组件卸载了');
    }
  },
};
