      person.job.type = '搬砖工'
      person.job.salary = '1k'
      person.hobby[0] = '学习'  // reactive 可以直接处理数组数据，不需要调用数组函数
    }

    function addSex() {
      person.sex = '男'
    }

    function deleteName() {
      delete person.name
    }

    return {
      person,
      changeInfo,
      addSex,
      deleteName
    }
  }
}
